class Solution:
    def longestPalindrome(self, s):
        l = len(s)
        if l == 1: return s
        maxn = 1
        maxs = s[0]
        for i in range(1,l):
            for j in range(0, min(i, l-1 -i)):
                if 2*min(i, l-1 -i) +1 < maxn: break
                if s[i - 1 - j] == s[i + 1 + j]:
                    if 2*j + 3 > maxn:
                        maxn = 2*j + 3
                        maxs = s[i-1-j: i+j+2]
                else: break
            for j in range(0, min(i, l-i)):
                if 2*min(i, l-i) < maxn: break
                if s[i - 1 - j] == s[i + j]:
                    if 2* j + 2> maxn:
                        maxn = 2*j + 2
                        maxs = s[i-1-j: i+j+1]
                else: break
        return maxs
                
